let counter = parseInt(document.getElementById('counter').innerText)
let plus = document.getElementById('plus')
let minus = document.getElementById('minus')
let like = document.getElementById('heart')
let likeString = document.getElementsByClassName('likes')[0].innerText
let pause = document.getElementById('pause')
let comment = document.querySelector('form')


timer = setInterval(autoCountUp, 1000)

plus.addEventListener('click', clickUp)
minus.addEventListener('click', clickDown)
like.addEventListener('click', likeNum)
pause.addEventListener('click', pauseCounter)
comment.addEventListener('submit', (e) => {
    e.preventDefault()
    console.log(e.target.comment_input.value)
    let p = document.createElement('p')
    p.textContent = e.target.comment_input.value
    document.getElementById('list').appendChild(p)
})


function autoCountUp() {
    counter++
    document.getElementById('counter').innerText = counter
}

function clickUp() {
    counter++
    document.getElementById('counter').innerText = counter
}

function clickDown() {
    counter--
    document.getElementById('counter').innerText = counter
}

function likeNum() {
    likeString = `The number ${counter} has been liked 1 time.`
    document.getElementsByClassName('likes')[0].innerText = likeString
}

function pauseCounter() {
    if (pause.innerText === "pause") {
        clearInterval(timer)
        plus.removeEventListener('click', clickUp)
        minus.removeEventListener('click', clickDown)
        like.removeEventListener('click', likeNum)
        pause.innerText = "resume"
    } else if (pause.innerText === 'resume') {
        timer = setInterval(autoCountUp, 1000)
        plus.addEventListener('click', clickUp)
        minus.addEventListener('click', clickDown)
        like.addEventListener('click', likeNum)
        pause.innerText = "pause"
    }
}